import pandas as pdclass recommendation:    def __init__(self):        self.data = pd.read_csv('data.csv')    def answers():        result = {}        result['Covered'] = input('Do you want to be under cover, or no preference? (y/n/np): ').lower()        result['Beer_Proximity'] = input('On scale of 1-3, how important is it that you be close to the beer garden or no preference? (3 = Very Important, 2 = Somewhat Important, 1 = I do not care): ')        result['Price_Max'] = input('What is the maximum price you would like to pay?: ')        result['Price_Min'] = input('What is the minimum price you would like to pay?: ')        result['Home_Away'] = input('Do you want to sit on the home side, away side, or no preference? (Home = 1, Away = 2, No Preference = 3): ')        return result    def filter_data(response):        result = self.data.copy()        if response['Covered'] == 'y':            result = result[result['Covered'] == 1]        elif response['Covered'] == 'n':            result = result[result['Covered'] == 0]        if response['Beer_Proximity'] == '3':            result = result[result['Beer_Rating'] > 7]        elif response['Beer_Proximity'] == '2':            result = result[result['Beer_Rating'] > 4]        result = result[(result['Price'] > int(response['Price_Min'])) & (result['Price'] < int(response['Price_Max']))]        if response['Home_Away'] == '1':            result = result[result['Home_Away'] == 1]        elif response['Home_Away'] == '2':            result = result[result['Home_Away'] == 0]        return result[['Section', 'Row', 'Price', 'Customer_Rating']].sort_values('Customer_Rating', ascending=False)if __name__ == '__main__':    response = answers()    result = filter_data(response)'''note need to look for more data sources and incorporate bathroom locations, also need to add try and except for the filtering of data add tick pick information regarding rating of seats and incorporate individual game information'''